package velo.ladealpha.fields.astronomy;

// TODO: Auto-generated Javadoc
/**
 * The Class AstronomicalUnitConversion.
 */
public class AstronomicalUnitConversion {
	
	/**
	 * A uto LY.
	 *
	 * @param in the in
	 * @return the double
	 */
	public static double AUtoLY(double in) {
		return in * (1.5813 * Math.pow(10, -5));
	}
}
